import React, { useEffect, useState } from "react";
import {
  Container,
  Typography,
  Box,
  Card,
  CardMedia,
  Divider,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import api from "../api/axios";
import { useParams, useNavigate } from "react-router-dom";

export default function RecipeDetail() {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const backendURL = "http://localhost:5000";
  const navigate = useNavigate();

  const token = localStorage.getItem("token");
  const user = JSON.parse(localStorage.getItem("user") || "{}");

  // ‚úÖ Fetch recipe by ID
  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const res = await api.get(`/recipes/${id}`);
        setRecipe(res.data);
      } catch (err) {
        console.error("‚ùå Error fetching recipe:", err);
      }
    };
    fetchRecipe();
  }, [id]);

  // ‚úÖ Delete recipe handler
  const handleDelete = async () => {
    try {
      await api.delete(`/recipes/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("‚úÖ Recipe deleted successfully!");
      navigate("/recipes");
    } catch (err) {
      console.error("‚ùå Error deleting recipe:", err);
      alert("Failed to delete recipe. Try again.");
    } finally {
      setOpenDialog(false);
    }
  };

  if (!recipe)
    return (
      <Typography align="center" mt={5}>
        Loading recipe details...
      </Typography>
    );

  return (
    <Container sx={{ mt: 5, mb: 5 }}>
      <Card sx={{ boxShadow: 5, borderRadius: 3, p: 3 }}>
        {recipe.image && (
          <CardMedia
            component="img"
            image={`${backendURL}/${recipe.image}`}
            alt={recipe.title}
            sx={{
              borderRadius: 2,
              mb: 3,
              maxHeight: 400,
              objectFit: "cover",
            }}
            onError={(e) => {
              e.target.onerror = null;
              e.target.src =
                "https://via.placeholder.com/600x400.png?text=Image+Not+Available";
            }}
          />
        )}

        <Typography variant="h4" fontWeight={700}>
          {recipe.title}
        </Typography>

        <Typography
          variant="subtitle1"
          color="text.secondary"
          sx={{ mt: 1, mb: 2 }}
        >
          üë®‚Äçüç≥ By {recipe.author?.name || "Anonymous"}
        </Typography>

        <Divider sx={{ my: 2 }} />

        <Typography
          variant="body1"
          sx={{ mb: 3, fontSize: "1rem", lineHeight: 1.6 }}
        >
          {recipe.description}
        </Typography>

        <Typography variant="h6" sx={{ mt: 3 }}>
          üßÇ Ingredients:
        </Typography>
        <Box component="ul" sx={{ pl: 3 }}>
          {recipe.ingredients.map((item, index) => (
            <li key={index}>
              <Typography variant="body2">{item}</Typography>
            </li>
          ))}
        </Box>

        <Typography variant="h6" sx={{ mt: 3 }}>
          üë®‚Äçüç≥ Steps:
        </Typography>
        <Box component="ol" sx={{ pl: 3 }}>
          {recipe.steps.map((step, index) => (
            <li key={index}>
              <Typography variant="body2">{step}</Typography>
            </li>
          ))}
        </Box>

        {/* ‚úÖ Delete button (only visible to recipe owner) */}
        {user?._id === recipe.author?._id && (
          <Box sx={{ mt: 4 }}>
            <Button
              variant="contained"
              color="error"
              startIcon={<DeleteIcon />}
              onClick={() => setOpenDialog(true)}
              sx={{ textTransform: "none", fontWeight: 600 }}
            >
              Delete Recipe
            </Button>
          </Box>
        )}
      </Card>

      {/* ‚úÖ Confirmation Dialog */}
      <Dialog
        open={openDialog}
        onClose={() => setOpenDialog(false)}
        aria-labelledby="delete-dialog-title"
      >
        <DialogTitle id="delete-dialog-title">Confirm Deletion</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to delete this recipe? This action cannot be
            undone.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)} color="primary">
            Cancel
          </Button>
          <Button onClick={handleDelete} color="error" variant="contained">
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}
