import React, { useState } from "react";
import { Container, TextField, Button, Typography, Snackbar } from "@mui/material";
import api from "../api/axios";

export default function AddRecipe() {
  const [form, setForm] = useState({
    title: "",
    description: "",
    ingredients: "",
    steps: "",
  });
  const [image, setImage] = useState(null);
  const [alert, setAlert] = useState(false);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    const data = new FormData();
    Object.keys(form).forEach((key) => data.append(key, form[key]));
    if (image) data.append("image", image);

    await api.post("/recipes", data, {
      headers: { Authorization: `Bearer ${token}`, "Content-Type": "multipart/form-data" },
    });
    setAlert(true);
    setForm({ title: "", description: "", ingredients: "", steps: "" });
    setImage(null);
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 5 }}>
      <Typography variant="h4" fontWeight={600} mb={3}>
        Add New Recipe
      </Typography>
      <form onSubmit={handleSubmit}>
        <TextField label="Title" name="title" fullWidth required margin="normal" value={form.title} onChange={handleChange} />
        <TextField label="Description" name="description" fullWidth multiline rows={3} margin="normal" value={form.description} onChange={handleChange} />
        <TextField label="Ingredients (comma separated)" name="ingredients" fullWidth margin="normal" value={form.ingredients} onChange={handleChange} />
        <TextField label="Steps (comma separated)" name="steps" fullWidth margin="normal" value={form.steps} onChange={handleChange} />
        <Button variant="contained" component="label" sx={{ mt: 2 }}>
          Upload Image
          <input hidden type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} />
        </Button>
        <Button type="submit" variant="contained" color="success" fullWidth sx={{ mt: 3 }}>
          Submit
        </Button>
      </form>
      <Snackbar open={alert} autoHideDuration={2000} message="Recipe added successfully!" onClose={() => setAlert(false)} />
    </Container>
  );
}
