import React from "react";
import {
  Box,
  Typography,
  Button,
  Container,
  Stack,
  Fade,
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import RestaurantMenuIcon from "@mui/icons-material/RestaurantMenu";
import AddCircleOutlineIcon from "@mui/icons-material/AddCircleOutline";

export default function Home() {
  const navigate = useNavigate();

  return (
    <Fade in={true} timeout={1000}>
      <Box
        sx={{
          minHeight: "100vh",
          background:
            "radial-gradient(circle at 20% 30%, #fce4ec, transparent 30%), radial-gradient(circle at 80% 70%, #e3f2fd, transparent 25%), linear-gradient(120deg, #fff1eb, #ace0f9)",
          backgroundSize: "400% 400%",
          animation: "brightShift 12s ease infinite",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          flexDirection: "column",
          textAlign: "center",
          px: 3,
          color: "#222",
          overflow: "hidden",
          position: "relative",
        }}
      >
        {/* ‚ú® Subtle Glow Overlay */}
        <Box
          sx={{
            position: "absolute",
            top: "-10%",
            left: "-10%",
            width: "120%",
            height: "120%",
            background:
              "radial-gradient(circle at center, rgba(255,255,255,0.3), transparent 70%)",
            zIndex: 0,
          }}
        />

        <Container sx={{ zIndex: 2 }}>
          {/* ======= Title ======= */}
          <Typography
            variant="h2"
            fontWeight={800}
            mb={2}
            sx={{
              background:
                "linear-gradient(90deg, #e52e71, #ff8a00, #9b00ff, #007aff)",
              WebkitBackgroundClip: "text",
              WebkitTextFillColor: "transparent",
              fontFamily: "'Poppins', sans-serif",
              letterSpacing: "1px",
            }}
          >
            Welcome to Dishcovery üç¥
          </Typography>

          {/* ======= Subtitle ======= */}
          <Typography
            variant="h6"
            mb={5}
            sx={{
              color: "rgba(0,0,0,0.75)",
              fontFamily: "'Inter', sans-serif",
              letterSpacing: "0.5px",
              maxWidth: "700px",
              margin: "0 auto",
              lineHeight: 1.6,
            }}
          >
            Discover new recipes, share your favorites, and connect with food
            lovers worldwide. Dishcovery is where creativity meets taste.
          </Typography>

          {/* ======= Buttons ======= */}
          <Stack
            direction={{ xs: "column", sm: "row" }}
            spacing={3}
            justifyContent="center"
          >
            <Button
              variant="contained"
              size="large"
              startIcon={<RestaurantMenuIcon />}
              onClick={() => navigate("/recipes")}
              sx={{
                background: "linear-gradient(45deg, #ff8a00, #e52e71)",
                color: "#fff",
                fontWeight: 600,
                px: 4,
                py: 1.5,
                borderRadius: 3,
                textTransform: "none",
                boxShadow: "0px 4px 15px rgba(229, 46, 113, 0.4)",
                "&:hover": {
                  transform: "scale(1.05)",
                  boxShadow: "0px 6px 20px rgba(229, 46, 113, 0.6)",
                  transition: "0.3s",
                },
              }}
            >
              Explore Recipes
            </Button>

            <Button
              variant="outlined"
              size="large"
              startIcon={<AddCircleOutlineIcon />}
              onClick={() => navigate("/add")}
              sx={{
                color: "#e52e71",
                borderColor: "#e52e71",
                fontWeight: 600,
                px: 4,
                py: 1.5,
                borderRadius: 3,
                textTransform: "none",
                "&:hover": {
                  background: "rgba(229, 46, 113, 0.1)",
                  borderColor: "#ff8a00",
                  transform: "scale(1.05)",
                  transition: "0.3s",
                },
              }}
            >
              Add New Recipe
            </Button>
          </Stack>

          {/* ======= Footer ======= */}
          <Box
            sx={{
              mt: 8,
              color: "rgba(0,0,0,0.6)",
              fontSize: 14,
              letterSpacing: 0.5,
              fontFamily: "'Inter', sans-serif",
            }}
          >
            Crafted with ‚ù§Ô∏è by <strong>Dishcovery Team</strong>
          </Box>
        </Container>

        {/* ======= Animation ======= */}
        <style>{`
          @keyframes brightShift {
            0% { background-position: 0% 50%; }
            50% { background-position: 100% 50%; }
            100% { background-position: 0% 50%; }
          }
        `}</style>
      </Box>
    </Fade>
  );
}
