import React, { useState } from "react";
import { Container, TextField, Button, Typography, Snackbar } from "@mui/material";
import api from "../api/axios";
import { useNavigate } from "react-router-dom";

export default function Register() {
  const [form, setForm] = useState({ name: "", email: "", password: "" });
  const [alert, setAlert] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    await api.post("/auth/register", form);
    setAlert(true);
    setTimeout(() => navigate("/login"), 2000);
  };

  return (
    <Container maxWidth="xs" sx={{ mt: 8 }}>
      <Typography variant="h4" fontWeight={600} mb={3} textAlign="center">
        Register
      </Typography>
      <form onSubmit={handleSubmit}>
        <TextField label="Name" name="name" fullWidth margin="normal" onChange={(e) => setForm({ ...form, name: e.target.value })} />
        <TextField label="Email" name="email" fullWidth margin="normal" onChange={(e) => setForm({ ...form, email: e.target.value })} />
        <TextField label="Password" name="password" type="password" fullWidth margin="normal" onChange={(e) => setForm({ ...form, password: e.target.value })} />
        <Button type="submit" fullWidth variant="contained" sx={{ mt: 2 }}>
          Register
        </Button>
      </form>
      <Snackbar open={alert} autoHideDuration={1500} message="Registered successfully!" />
    </Container>
  );
}
