import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Container, Typography, Box, Card, CardMedia, Divider } from "@mui/material";
import api from "../api/axios";

export default function RecipeDetail() {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);
  const backendURL = "http://localhost:5000";

  useEffect(() => {
    api.get(`/recipes/${id}`).then((res) => setRecipe(res.data));
  }, [id]);

  if (!recipe) return <Typography align="center" mt={5}>Loading...</Typography>;

  return (
    <Container sx={{ mt: 5, mb: 5 }}>
      <Card sx={{ boxShadow: 5, borderRadius: 3, p: 3 }}>
        {recipe.image && (
          <CardMedia
            component="img"
            image={`${backendURL}/${recipe.image}`}
            alt={recipe.title}
            sx={{
              borderRadius: 2,
              mb: 3,
              maxHeight: 400,
              objectFit: "cover",
            }}
          />
        )}
        <Typography variant="h4" fontWeight={700}>
          {recipe.title}
        </Typography>
        <Typography variant="subtitle1" color="text.secondary" sx={{ mt: 1 }}>
          By {recipe.author?.name || "Anonymous"}
        </Typography>

        <Divider sx={{ my: 2 }} />

        <Typography variant="body1" sx={{ mb: 2 }}>
          {recipe.description}
        </Typography>

        <Typography variant="h6" sx={{ mt: 3 }}>
          üßÇ Ingredients:
        </Typography>
        <Box component="ul" sx={{ pl: 3 }}>
          {recipe.ingredients.map((item, index) => (
            <li key={index}>
              <Typography variant="body2">{item}</Typography>
            </li>
          ))}
        </Box>

        <Typography variant="h6" sx={{ mt: 3 }}>
          üë®‚Äçüç≥ Steps:
        </Typography>
        <Box component="ol" sx={{ pl: 3 }}>
          {recipe.steps.map((step, index) => (
            <li key={index}>
              <Typography variant="body2">{step}</Typography>
            </li>
          ))}
        </Box>
      </Card>
    </Container>
  );
}
