import React from "react";
import {
  Box,
  Typography,
  Button,
  Container,
  Stack,
  Fade,
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import RestaurantMenuIcon from "@mui/icons-material/RestaurantMenu";
import AddCircleOutlineIcon from "@mui/icons-material/AddCircleOutline";

export default function Home() {
  const navigate = useNavigate();

  return (
    <Fade in={true} timeout={1000}>
      <Box
        sx={{
          minHeight: "100vh",
          background: "linear-gradient(135deg, #1a1a1a 40%, #343a40 100%)",
          color: "#fff",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          flexDirection: "column",
          textAlign: "center",
          px: 3,
        }}
      >
        <Container>
          <Typography
            variant="h2"
            fontWeight={700}
            mb={2}
            sx={{
              background:
                "linear-gradient(90deg, #ff8a00, #e52e71, #9b00ff, #007aff)",
              WebkitBackgroundClip: "text",
              WebkitTextFillColor: "transparent",
              fontFamily: "Poppins, sans-serif",
            }}
          >
            Welcome to Dishcovery üçΩÔ∏è
          </Typography>

          <Typography
            variant="h6"
            mb={5}
            color="rgba(255,255,255,0.8)"
            sx={{
              fontFamily: "Inter, sans-serif",
              letterSpacing: "0.5px",
            }}
          >
            Share your favorite recipes, explore new dishes, and connect with food lovers worldwide.
          </Typography>

          <Stack
            direction={{ xs: "column", sm: "row" }}
            spacing={3}
            justifyContent="center"
          >
            <Button
              variant="contained"
              size="large"
              startIcon={<RestaurantMenuIcon />}
              onClick={() => navigate("/recipes")}
              sx={{
                background: "linear-gradient(45deg, #ff8a00, #e52e71)",
                color: "#fff",
                fontWeight: 600,
                px: 4,
                py: 1.5,
                textTransform: "none",
                borderRadius: 2,
                "&:hover": {
                  background: "linear-gradient(45deg, #e67e22, #d6306e)",
                  transform: "scale(1.05)",
                  transition: "0.3s",
                },
              }}
            >
              Explore Recipes
            </Button>

            <Button
              variant="outlined"
              size="large"
              startIcon={<AddCircleOutlineIcon />}
              onClick={() => navigate("/add")}
              sx={{
                color: "#fff",
                borderColor: "#e52e71",
                fontWeight: 600,
                px: 4,
                py: 1.5,
                textTransform: "none",
                borderRadius: 2,
                "&:hover": {
                  background: "rgba(255, 255, 255, 0.1)",
                  borderColor: "#ff8a00",
                  transform: "scale(1.05)",
                  transition: "0.3s",
                },
              }}
            >
              Add New Recipe
            </Button>
          </Stack>

          <Box
            sx={{
              mt: 8,
              color: "rgba(255,255,255,0.5)",
              fontSize: 14,
              letterSpacing: 0.5,
            }}
          >
            Crafted with ‚ù§Ô∏è by Dishcovery Team
          </Box>
        </Container>
      </Box>
    </Fade>
  );
}
