import React, { useState } from "react";
import { Container, TextField, Button, Typography, Snackbar } from "@mui/material";
import api from "../api/axios";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [form, setForm] = useState({ email: "", password: "" });
  const [alert, setAlert] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post("/auth/login", form);
      localStorage.setItem("token", res.data.token);
      setAlert(true);
      setTimeout(() => navigate("/recipes"), 1500);
    } catch {
      alert("Invalid credentials");
    }
  };

  return (
    <Container maxWidth="xs" sx={{ mt: 8 }}>
      <Typography variant="h4" fontWeight={600} mb={3} textAlign="center">
        Login
      </Typography>
      <form onSubmit={handleSubmit}>
        <TextField label="Email" name="email" fullWidth margin="normal" onChange={(e) => setForm({ ...form, email: e.target.value })} />
        <TextField label="Password" type="password" name="password" fullWidth margin="normal" onChange={(e) => setForm({ ...form, password: e.target.value })} />
        <Button type="submit" fullWidth variant="contained" sx={{ mt: 2 }}>
          Login
        </Button>
      </form>
      <Snackbar open={alert} autoHideDuration={1500} message="Login successful!" />
    </Container>
  );
}
