import React from "react";
import {
  Box,
  Typography,
  Button,
  Container,
  Stack,
  Fade,
  Paper
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import RestaurantMenuIcon from "@mui/icons-material/RestaurantMenu";
import AddCircleOutlineIcon from "@mui/icons-material/AddCircleOutline";

export default function Home() {
  const navigate = useNavigate();

  return (
    <Fade in={true} timeout={1000}>
      <Box
        sx={{
          minHeight: "100vh",
          width: "100vw",
          position: "relative",
          overflow: "hidden",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          background:
            "linear-gradient(135deg, #0f0c29, #302b63, #24243e 60%, #fc00ff 100%)",
          // Neon color shifts
          backgroundSize: "200% 200%",
          animation: "neonGradient 10s ease-in-out infinite"
        }}
      >
        <style>{`
          @keyframes neonGradient {
            0% {
              background-position: 0% 50%;
            }
            50% {
              background-position: 100% 50%;
            }
            100% {
              background-position: 0% 50%;
            }
          }
        `}</style>

        {/* Material UI White Glass Card */}
        <Container
          sx={{
            zIndex: 1,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center"
          }}
        >
          <Paper
            elevation={8}
            sx={{
              px: { xs: 3, md: 7 },
              py: { xs: 6, md: 9 },
              mt: { xs: 6, md: 0 },
              background: "rgba(255,255,255,0.94)",
              borderRadius: "28px",
              boxShadow:
                "0 12px 48px 0 rgba(130, 36, 227, 0.12), 0 1.5px 10px 0 rgba(0,204,255,0.1)",
              backdropFilter: "blur(3px)",
              minWidth: { xs: "88vw", md: 600 },
              maxWidth: 720,
              textAlign: "center"
            }}
          >
            <Typography
              variant="h2"
              fontWeight={800}
              mb={2}
              sx={{
                background:
                  "linear-gradient(90deg, #e52e71, #ff8a00, #9b00ff, #00e6e6)",
                WebkitBackgroundClip: "text",
                WebkitTextFillColor: "transparent",
                fontFamily: "'Poppins', sans-serif",
                letterSpacing: "1.2px"
              }}
            >
              Welcome to Dishcovery <span role="img" aria-label="Icon">üç¥</span>
            </Typography>
            <Typography
              variant="h6"
              mb={5}
              sx={{
                color: "#3A3A3A",
                fontFamily: "'Inter', sans-serif",
                letterSpacing: "0.5px",
                maxWidth: "650px",
                margin: "0 auto",
                lineHeight: 1.7,
                opacity: 0.92
              }}
            >
              Discover new recipes, share your favorites, and connect with food lovers worldwide. Dishcovery is where creativity meets taste.
            </Typography>
            <Stack
              direction={{ xs: "column", sm: "row" }}
              spacing={3}
              justifyContent="center"
              sx={{ mb: 3 }}
            >
              <Button
                variant="contained"
                size="large"
                startIcon={<RestaurantMenuIcon />}
                onClick={() => navigate("/recipes")}
                sx={{
                  background: "linear-gradient(45deg, #ff8a00, #e52e71)",
                  color: "#fff",
                  fontWeight: 600,
                  px: 4,
                  py: 1.5,
                  borderRadius: 3,
                  textTransform: "none",
                  boxShadow: "0px 2px 15px #e52e7177",
                  "&:hover": {
                    background: "linear-gradient(45deg, #e52e71, #ff8a00)",
                    boxShadow: "0px 6px 25px #ff8a0080",
                    transform: "scale(1.05)",
                  }
                }}
              >
                Explore Recipes
              </Button>
              <Button
                variant="outlined"
                size="large"
                startIcon={<AddCircleOutlineIcon />}
                onClick={() => navigate("/add")}
                sx={{
                  color: "#9b00ff",
                  borderColor: "#9b00ff",
                  fontWeight: 600,
                  px: 4,
                  py: 1.5,
                  borderRadius: 3,
                  textTransform: "none",
                  background: "rgba(237,0,255,0.03)",
                  "&:hover": {
                    background: "rgba(155, 0, 255, 0.09)",
                    borderColor: "#00e6e6",
                    color: "#007aff",
                    transform: "scale(1.06)",
                  }
                }}
              >
                Add New Recipe
              </Button>
            </Stack>
            <Box
              sx={{
                mt: 8,
                color: "rgba(0,0,0,0.55)",
                fontSize: 14,
                letterSpacing: 0.5,
                fontFamily: "'Inter', sans-serif"
              }}
            >
              Crafted with <span style={{ color: "#e52e71" }}>‚ù§Ô∏è</span> by <strong>Dishcovery Team</strong>
            </Box>
          </Paper>
        </Container>
      </Box>
    </Fade>
  );
}
